var gulp = require('gulp')
  , mocha = require('gulp-mocha')
  , env = require('gulp-env');

gulp.task('test', function() {
  env({ vars: { NODE_ENV: 'test' }});

  return gulp.src('./test/**/*.js')
  .pipe(mocha({
    globals   : [ ]
  , reporter  : 'spec'
  , ui        : 'bdd'
  , growl     : true
  , recursive : true
  , timeout   : 1000
  }));
});